mapper.sql:
FROM 
	pathy.codegroup a
WHERE 1 = 1

	AND cgDelNy = #{shDelNy}

	AND cgUseNy = #{shUseNy}


		AND a.cgDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
		AND a.cgDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}




		AND cgSeq = #{shValue}
		AND cgName LIKE CONCAT('%', #{shValue},'%')

mapper.delete:
DELETE FROM pathy.codegroup
WHERE 1=1
	AND cgSeq = #{cgSeq}
