mapper.sql:
from
	qna a
left join
	customer b
on
	a.customer_cmSeq = b.cmSeq
left join
	insMember c
on
	a.insMember_insmSeq = c.insmSeq
WHERE 1=1	
And qnaDelNY = #{shDelNY}
And qnaCategory = #{shCategory}


	AND a.qnaDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.qnaDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}




	AND cmName LIKE CONCAT('%',#{shValue},'%') AND cmName = #{shValue}
	AND insmName = #{shValue}
	AND qnaTitle LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
		insert into qna(
			qnaCategory
			,qnaDelNY
			,qnaDateTimeReg
			,qnaDateTimeEdit
			,qnaTitle
			,qnaContent
			,customer_cmSeq
			,insMember_insmSeq
		)values(
    		#{qnaCategory}
    		,0
    		,now()
    		,now()
    		,#{qnaTitle}
    		,#{qnaContent}
    		,#{customer_cmSeq}
    		,#{insMember_insmSeq}
		)

			SELECT last_Insert_id()

mapper.update:
update qna
set
	qnaCategory = #{qnaCategory}
	,qnaDelNY = #{qnaDelNY}
	,qnaTitle = #{qnaTitle}
	,qnaContent = #{qnaContent}
	,customer_cmSeq = #{customer_cmSeq}
	,insMember_insmSeq = #{insMember_insmSeq}
where 1 = 1
	and qnaSeq = #{qnaSeq}

mapper.delete:
delete from qna
where 1=1
	and qnaSeq = #{qnaSeq}
