mapper.sql:
FROM
	applicationShin AS a
LEFT JOIN insProduct AS b ON b.inspSeq = a.insProduct_inspSeq
LEFT JOIN customer AS c ON c.cmSeq = a.customer_cmSeq
LEFT JOIN insMember AS d ON d.insmSeq = a.insMember_insmSeq
WHERE 1 = 1
	AND shinDelNy = #{shDelNy}

		AND shinSeq = #{shValue}


		AND shinDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
		AND shinDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO applicationShin(
	shinDate
	,shinDateGa
	,shinDelNy
	,shinDateTimeReg
	,shinDateTimeEdit
	,insProduct_inspSeq
	,customer_cmSeq
	,insMember_insmSeq
)
VALUE(
	#{shinDate}
    ,#{shinDateGa}
    ,0
    ,now()
    ,now()
    ,#{insProduct_inspSeq}
    ,#{customer_cmSeq}
    ,#{insMember_insmSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE applicationShin
	SET
		shinDate = #{shinDate}
		,shinDateGa = #{shinDateGa}
		,shinDelNy = 0
		,shinDateTimeReg = now()
		,shinDateTimeEdit = now()
	WHERE 1 = 1
		AND shinSeq = #{shinSeq}

mapper.delete:
DELETE FROM applicationShin
	WHERE 1 = 1
	AND shinSeq = #{shinSeq}
