mapper.sql:
from
	claimManage b left join insMember a on a.insmSeq = b.insMember_insmSeq
				  left join claim c on c.claimManage_clmSeq = b.clmSeq
				  left join customer f on f.cmSeq = c.customer_cmSeq
where 1 = 1
And clmDelNY = #{shDelNY}
And clmUseNY = #{shUseNY}


	AND b.clmDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND b.clmDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



	AND clmSeq = #{shValue} 
	AND clmName LIKE CONCAT('%',#{shValue},'%') AND insmName = #{shValue}
	AND clmOrder = #{shValue}

mapper.insert:
		insert into claimManage(
			clmName
			,clmSituation
			,clmDateTimeReg
			,clmDateTimeEdit
			,clmDateTimeAssign
			,clmUseNY
			,clmDelNY
			,insMember_insmSeq
		)values(
    		#{clmName}
    		,#{clmSituation}
    		,now()
    		,now()
    		,now()
    		,#{clmUseNY}
    		,0
    		,#{insMember_insmSeq}
		)

			SELECT last_Insert_id()

mapper.update:
update claimManage
set
	clmName = #{clmName}
	,clmSituation = #{clmSituation}
	,clmUseNY = #{clmUseNY}
	,clmDelNY = #{clmDelNY}
where 1 = 1
	and clmSeq = #{clmSeq}

mapper.delete:
delete from claimManage
where 1=1
	and clmSeq = #{clmSeq}
