mapper.sql:
from
	claimManage a
where 1 = 1
And clmDelNY = #{shDelNY}
And clmUseNY = #{shUseNY}


	AND a.clmDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.clmDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



	AND a.clmSeq = #{shValue} 
	AND clmName LIKE CONCAT('%',#{shValue},'%') AND insmName = #{shValue}
	AND clmOrder = #{shValue}

mapper.insert:
		insert into claimManage(
			clmName
			,clmSituation
			,clmDateTimeReg
			,clmDateTimeEdit
			,clmUseNY
			,clmDelNY
		)values(
    		#{clmName}
    		,#{clmSituation}
    		,now()
    		,now()
    		,#{clmUseNY}
    		,0
		)

			SELECT last_Insert_id()

mapper.update:
update claimManage
set
	clmName = #{clmName}
	,clmSituation = #{clmSituation}
	,clmUseNY = #{clmUseNY}
	,clmDelNY = #{clmDelNY}
where 1 = 1
	and clmSeq = #{clmSeq}

mapper.delete:
delete from claimManage
where 1=1
	and clmSeq = #{clmSeq}
