mapper.sql:
from
	claim c left join customer a on a.cmSeq = c.customer_cmSeq
	left join claimManage b on b.clmSeq = c.claimManage_clmSeq
where 1 = 1
And clDelNY = #{shDelNY}
And clUseNY = #{shUseNY}
And clType = #{shType}
And clSituation = #{shSituation}
And clmSituation = #{shmSituation}


	AND c.clDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND c.clDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



	AND clSeq = #{shValue} 
	AND clTitle LIKE CONCAT('%',#{shValue},'%')
	AND cmSeq = #{shValue}
	AND cmName LIKE CONCAT('%',#{shValue},'%')

mapper.insert:
		insert into claim(
			clType
			,clTitle
			,clDec
			,clSituation
			,clDateTimeReg
			,clDateTimeEdit
			,clDateTimeTre
			,clDateTimeTreEnd
			,clDateTimePay
			,clPayOut
			,clPayMethod
			,clUseNY
			,clDelNY
			,claimManage_clmSeq
            ,customer_cmSeq
		)values(
    		#{clType}
    		,#{clTitle}
    		,#{clDec}
    		,#{clSituation}
    		,now()
    		,now()
    		,now()
    		,now()
    		,now()
    		,#{clPayOut}
    		,#{clPayMethod}
    		,#{clUseNY}
    		,0
    		,#{claimManage_clmSeq}
    		,#{customer_cmSeq}
		)

			SELECT last_Insert_id()

mapper.update:
update claim
set
	clType = #{clType}
	,clSituation = #{clSituation}
	,clUseNY = #{clUseNY}
where 1 = 1
	and clSeq = #{clSeq}

mapper.delete:
delete from claim
where 1=1
	and clSeq = #{clSeq}
