mapper.sql:
from
	claim a
where 1 = 1
And clDelNY = #{shDelNY}
And clUseNY = #{shUseNY}


	AND a.clDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.clDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



	AND a.clSeq = #{shValue} 
	AND clName LIKE CONCAT('%',#{shValue},'%') AND insmName = #{shValue}
	AND clOrder = #{shValue}

mapper.update:
update claimManage
set
	clmName = #{clmName}
	,clmSituation = #{clmSituation}
	,clmUseNY = #{clmUseNY}
	,clmDelNY = #{clmDelNY}
where 1 = 1
	and clmSeq = #{clmSeq}

mapper.delete:
delete from claimManage
where 1=1
	and clmSeq = #{clmSeq}
