mapper.sql:
from
	customerContract a
left join 
	insProduct b
on
	 a.insProduct_inspSeq = b.inspSeq
left join
	customer c
on
	a.customer_cmSeq = c.cmSeq
where 1 = 1
AND cmcDelNY = #{shDelNY}
AND cmcStatus = #{shStatus}



	AND a.cmDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.cmDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



	AND cmName = #{shValue}
	AND inspName = #{shValue}

mapper.update:
update customerContract
set
	cmcDelNy = #{cmcDelNy}
	,cmcStatus = #{cmcStatus}
	,insProduct_inspSeq = #{insProduct_inspSeq}
	,customer_cmSeq = #{customer_cmSeq}
where 1 = 1
	and cmcSeq = #{cmcSeq}

mapper.delete:
delete from customerContract
where 1=1
	and cmcSeq = #{cmcSeq}
