mapper.sql:
from
	customerContract a
where 1 = 1
AND cmDelNY = #{shDelNY}
AND cmGender = #{shGender}

And cmDelNY = #{shDelNY}
And cmUseNY = #{shUseNY}


	AND a.cmDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.cmDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



	AND cmName LIKE CONCAT('%',#{shValue},'%') AND cmName = #{shValue}
	AND cmOrder = #{shValue}
	AND cmId LIKE CONCAT('%',#{shValue},'%')
	AND cmEmail LIKE CONCAT('%',#{shValue},'%') AND cmEmail = #{shValue}
	AND cmPw LIKE CONCAT('%',#{shValue},'%') AND cmPw = #{shValue}
	AND cmSoNum LIKE CONCAT('%',#{shValue},'%') AND cmSoNum = #{shValue}
	AND cmPhone LIKE CONCAT('%',#{shValue},'%') AND cmPhone = #{shValue}
	AND cmAdr LIKE CONCAT('%',#{shValue},'%') AND cmAdr = #{shValue}
	AND cmAdrDt LIKE CONCAT('%',#{shValue},'%') AND cmAdrDt = #{shValue}
	AND cmAcc LIKE CONCAT('%',#{shValue},'%') AND cmAcc = #{shValue}

mapper.insert:
		insert into customerContract(
			cmName
			,cmOrder
			,cmGender
			,cmId
			,cmEmail
			,cmPw
			,cmSoNum
			,cmPhone
			,cmDesc
			,cmDateTimeReg
			,cmDateTimeEdit
			,cmUseNY
			,cmDelNY
			,cmZipNum
			,cmAdr
			,cmAdrDt
			,cmAcc
			,cmBank
		)values(
    		#{cmName}
    		,#{cmOrder}
    		,#{cmGender}
    		,#{cmId}
    		,#{cmEmail}
    		,#{cmPw}
    		,#{cmSoNum}
    		,#{cmPhone}
    		,#{cmDesc}
    		,now()
    		,now()
    		,1
    		,0
    		,#{cmZipNum}
    		,#{cmAdr}
    		,#{cmAdrDt}
    		,#{cmAcc}
    		,#{cmBank}
		)

			SELECT last_Insert_id()

mapper.update:
update customerContract
set
	cmName = #{cmName}
	,cmOrder = #{cmOrder}
	,cmGender = #{cmGender}
	,cmId = #{cmId}
	,cmEmail = #{cmEmail}
	,cmPw = #{cmPw}
	,cmSoNum = #{cmSoNum}
	,cmPhone = #{cmPhone}
	,cmDesc = #{cmDesc}
	,cmId = #{cmId}
	,cmUseNY = #{cmUseNY}
	,cmDelNY = 0
	,cmZipNum = #{cmZipNum}
	,cmAdr = #{cmAdr}
	,cmAdrDt = #{cmAdrDt}
	,cmAcc = #{cmAcc}
	,cmBank = #{cmBank}
where 1 = 1
	and cmSeq = #{cmSeq}

mapper.delete:
delete from customerContract
where 1=1
	and cmSeq = #{cmSeq}
