mapper.sql:
		FROM
			insContract AS a
		LEFT JOIN insProduct AS b ON b.inspSeq = a.insProduct_inspSeq
        LEFT JOIN customer AS c ON c.cmSeq = a.customer_cmSeq
		WHERE 1 = 1
			AND inscDelNy = #{shDelNy}

				AND inscSeq = #{shValue}
				AND inspName LIKE CONCAT('%', #{shValue}, '%')
				AND cmName LIKE CONCAT('%', #{shValue}, '%')


				AND inscDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
				AND inscDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO insContract(
	inscDateStart
	,inscDateEnd
	,inscStatus
	,inscBigo
	,inscDelNy
	,inscDateTimeReg
	,inscDateTimeEdit
	,insProduct_inspSeq
	,customer_cmSeq
)
VALUE(
	#{inscDateStart}
    ,#{inscDateEnd}
    ,#{inscStatus}
    ,#{inscBigo}
    ,0
    ,now()
    ,now()
    ,#{insProduct_inspSeq}
    ,#{customer_cmSeq}
)

	SELECT last_insert_id()

mapper.update:
UPDATE insContract
	SET
		inscDateStart = #{inscDateStart}
		,inscDateEnd = #{inscDateEnd}
		,inscStatus = #{inscStatus}
		,inscBigo = #{inscBigo}
		,inscDelNy = 0
		,inscDateTimeReg = now()
		,inscDateTimeEdit = now()
		,insProduct_inspSeq = #{insProduct_inspSeq}
		,customer_cmSeq = #{customer_cmSeq}
	WHERE 1 = 1
		AND inscSeq = #{inscSeq}

mapper.delete:
DELETE FROM insContract
	WHERE 1 = 1
	AND inscSeq = #{inscSeq}
