mapper.sql:
from
	insMember a
where 1 = 1
And insmDelNY = #{shDelNY}
And insmUseNY = #{shUseNY}


	AND a.insmDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.insmDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}



	AND a.insmSeq = #{shValue} 
	AND insmName LIKE CONCAT('%',#{shValue},'%') AND insmName = #{shValue}
	AND insmOrder = #{shValue}

mapper.insert:
		insert into insMember(
			insmName
			,insmOrder
			,insmGender
			,insmId
			,insmEmail
			,insmPw
			,insmSoNum
			,insmPhone
			,insmDesc
			,insmDateTimeReg
			,insmDateTimeEdit
			,insmUseNY
			,insmDelNY
			,insmClmNy
			,insmZipNum
			,insmAdr
			,insmAdrDt
		)values(
    		#{insmName}
    		,#{insmOrder}
    		,#{insmGender}
    		,#{insmId}
    		,#{insmEmail}
    		,#{insmPw}
    		,#{insmSoNum}
    		,#{insmPhone}
    		,#{insmDesc}
    		,now()
    		,now()
    		,#{insmUseNY}
    		,0
    		,#{insmClmNy}
    		,#{insmZipNum}
    		,#{insmAdr}
    		,#{insmAdrDt}
		)

			SELECT last_Insert_id()

mapper.update:
update insMember
set
	insmName = #{insmName}
	,insmOrder = #{insmOrder}
	,insmGender = #{insmGender}
	,insmId = #{insmId}
	,insmEmail = #{insmEmail}
	,insmPw = #{insmPw}
	,insmSoNum = #{insmSoNum}
	,insmPhone = #{insmPhone}
	,insmDesc = #{insmDesc}
	,insmUseNY = #{insmUseNY}
	,insmClmNy = #{insmClmNy}
	,insmZipNum = #{insmZipNum}
	,insmAdr = #{insmAdr}
	,insmAdrDt = #{insmAdrDt}
where 1 = 1
	and insmSeq = #{insmSeq}

mapper.delete:
delete from insMember
where 1=1
	and insmSeq = #{insmSeq}
