mapper.sql:
FROM
	insProduct AS a
WHERE 1 = 1
	AND inspDelNy = #{shDelNy}

		AND inspSeq = #{shValue}
		AND inspName LIKE CONCAT('%', #{shValue}, '%')


		AND inspDateTimeReg BETWEEN #{shDateStart} AND #{shDateEnd}
		AND inspDateTimeEdit BETWEEN #{shDateStart} AND #{shDateEnd}

mapper.insert:
INSERT INTO insProduct(
	inspName
	,inspBojang
	,inspJogun
	,inspPrice
	,inspBohumDateStart
	,inspBohumDateEnd
	,inspBojangDateStart
	,inspBojangDateEnd
	,inspPaper
	,inspDelNy
	,inspDateTimeReg
	,inspDateTimeEdit
)
VALUE(
	#{inspName}
    ,#{inspBojang}
    ,#{inspJogun}
    ,#{inspPrice}
    ,#{inspBohumDateStart}
    ,#{inspBohumDateEnd}
    ,#{inspBojangDateStart}
    ,#{inspBojangDateEnd}
    ,#{inspPaper}
    ,0
    ,now()
    ,now()
)

	SELECT last_insert_id()

mapper.update:
UPDATE insProduct
	SET
		inspName = #{inspName}
		,inspBojang = #{inspBojang}
		,inspJogun = #{inspJogun}
		,inspPrice = #{inspPrice}
		,inspBohumDateStart = #{inspBohumDateStart}
		,inspBohumDateEnd = #{inspBohumDateEnd}
		,inspBojangDateStart = #{inspBojangDateStart}
		,inspBojangDateEnd = #{inspBojangDateEnd}
		,inspPaper = #{inspPaper}
		,inspDelNy = 0
		,inspDateTimeReg = now()
		,inspDateTimeEdit = now()
	WHERE 1 = 1
		AND inspSeq = #{inspSeq}

mapper.delete:
DELETE FROM insProduct
	WHERE 1 = 1
	AND inspSeq = #{inspSeq}
